// experienceData.js

const experiences = [
    {
      picture: "modeln.jpeg",
      company: "Model N",
      role: "Python Programmer Intern",
      location: "San Mateo, CA",
      time: "Jun 2023 - Aug 2023",
      bullets: [
        "Worked on Model N's finance team, developed and deployed tools for recognizing and extracting data from PDF contracts and for customer record mapping.",
        "Automated contract data extraction with a 90% success rate across 234 documents by leveraging Python, Numpy, and Pandas, leading to more accurate reporting",
        "Increased data consistency by mapping 116 customer records across AWS and Salesforce, enhancing the unified view of customers.",
        "Reduced manual effort by 80% in data entry processes through the development of a Python CLI tool that automated file management and Excel data population."
      ]
    },
    {
      picture: "ndsu.png",
      company: "North Dakota State University (NDSU)",
      role: "Undergradute Researcher",
      location: "Fargo, ND",
      time: "May 2021 - Aug 2021",
      bullets: [
        "Participated in the Research Experience for Undergraduates (REU) program, using Gradient Descent Trained Expert System (GDES) to build a fake news detection expert system and authored research papers.",
        "Increased fake news detection accuracy by 12% from previous works, achieving a best accuracy of 62.4% by developing a decision-making network using GDES.",
        "Enhanced data processing skills for training the decision-making network.",
        "Authored: [url=https://www.mdpi.com/1424-8220/21/21/7083/htm]Deceptive Online Content Detection Using Only Message Characteristics and a Machine Learning Trained Expert System[/url], on Sensors 2021, 21(21), 7083.",
        "Co-authored: [url=https://arxiv.org/abs/2108.08264]Fake News and Phishing Detection Using a Machine Learning Trained Expert System[/url], and contributed to Information."
      ]
    },
    {
      picture: "uab.png",
      company: "The University of Alabama at Birmingham",
      role: "Undergraduate Researcher",
      location: "Birmingham, AL",
      time: "Oct 2020 - Jan 2021",
      bullets: [
        "Contributed to a computer vision project focusing on Drone-Based Tower Survey, implementing a detection model to locate tower issues from drone video frames using a multi-task learning scheme.",
        "Enhanced problem detection recall by 20%, improving from 59.6% to 71.5% through a multi-task learning approach that integrates object detection and feature extraction using Pytorch and TensorFlow.",
        "Performed precise image annotation using Image Annotator to label components like bolts, nuts, washers, and pins, which improved the quality of extracted features and enhanced detection performance.",
        "Completed a project jointly and a poster P207 titled [url=https://ieeexplore.ieee.org/document/9672078]Drone-Based Tower Survey by Multi-Task Learning [/url]which is accepted in the 2021 IEEE International Conference on Big Data."
      ]
    }
    // Add more experiences as needed
  ];
  
  export default experiences;
